// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title PredictionBattle
 * @dev A simple prediction market contract for Farcaster frames.
 * Admin creates markets. Users bet ETH/Native Token on YES/NO.
 * Admin resolves markets. Winners claim their share.
 */
contract PredictionBattle {
    address public admin;
    uint256 public creatorFeeBps = 500; // 5% fee for market creator
    uint256 public platformFeeBps = 1000; // 10% platform fee

    struct Prediction {
        string id;
        address creator; // Market Creator
        uint256 target;
        uint256 deadline;
        bool resolved;
        bool result; // true = YES, false = NO
        uint256 totalYes;
        uint256 totalNo;
        
        // Dead Liquidity Tracking (Seed)
        uint256 seedYes;
        uint256 seedNo;

        // Tracking bettors for auto-distribution
        address[] yesBettors;
        address[] noBettors;
        mapping(address => uint256) yesBets;
        mapping(address => uint256) noBets;
        // Payout tracking
        bool isVoid; // New: Refund/Draw flag
        uint256 processedIndex; // To track how many winners have been paid (for batching)
        bool paidOut;
    }

    mapping(string => Prediction) public predictions;
    mapping(string => bool) public predictionExists;

    mapping(address => bool) public operators; // Authorized auto-verifiers

    event PredictionCreated(string id, address creator, uint256 target, uint256 deadline);
    event SeedAdded(string id, uint256 seedYes, uint256 seedNo);
    event BetPlaced(string id, address user, bool vote, uint256 amount);
    event PredictionResolved(string id, bool result, uint256 winnerPool, uint256 platformFee, uint256 creatorFee);
    event PredictionVoided(string id, uint256 totalPool, uint256 platformFee);
    event PayoutDistributed(string id, address user, uint256 amount);
    event DistributionCompleted(string id);
    event OperatorUpdated(address operator, bool status);

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    modifier onlyAdminOrOperator() {
        require(msg.sender == admin || operators[msg.sender], "Not authorized");
        _;
    }

    constructor() {
        admin = msg.sender;
    }

    // 0. Manage Operators
    function setOperator(address _operator, bool _status) external onlyAdmin {
        operators[_operator] = _status;
        emit OperatorUpdated(_operator, _status);
    }

    // 1. Create a Prediction Market (Permissionless + Seed in one Tx)
    function createPrediction(string memory _id, uint256 _target, uint256 _duration) external payable {
        require(!predictionExists[_id], "Prediction ID already exists");
        require(msg.value > 0 && msg.value % 2 == 0, "Seed must be > 0 and even");
        
        Prediction storage p = predictions[_id];
        p.id = _id;
        p.creator = msg.sender;
        p.target = _target;
        p.deadline = block.timestamp + _duration;
        p.resolved = false;
        p.isVoid = false;
        p.processedIndex = 0;
        p.paidOut = false;
        
        // Auto-Seed Split (Dead Liquidity)
        uint256 seedPerSide = msg.value / 2;
        p.totalYes = seedPerSide;
        p.totalNo = seedPerSide;
        p.seedYes = seedPerSide;
        p.seedNo = seedPerSide;
        
        predictionExists[_id] = true;
        
        emit PredictionCreated(_id, msg.sender, _target, p.deadline);
        emit SeedAdded(_id, seedPerSide, seedPerSide);
    }

    // 1.5 Additional Seed (Optional Boost)
    function seedPrediction(string memory _id) external payable { // Simplified signature
        require(predictionExists[_id], "Prediction does not exist");
        require(msg.value > 0 && msg.value % 2 == 0, "Seed must be > 0 and even");
        
        uint256 seedPerSide = msg.value / 2;

        Prediction storage p = predictions[_id];
        
        p.totalYes += seedPerSide;
        p.totalNo += seedPerSide;
        p.seedYes += seedPerSide;
        p.seedNo += seedPerSide;
        
        emit SeedAdded(_id, seedPerSide, seedPerSide);
    }

    // 2. Place a Bet
    function placeBet(string memory _id, bool _vote) external payable {
        require(predictionExists[_id], "Prediction does not exist");
        Prediction storage p = predictions[_id];
        require(block.timestamp < p.deadline, "Betting period expired");
        require(msg.value > 0, "Bet amount must be greater than 0");

        if (_vote) {
            if (p.yesBets[msg.sender] == 0) {
                p.yesBettors.push(msg.sender);
            }
            p.yesBets[msg.sender] += msg.value;
            p.totalYes += msg.value;
        } else {
            if (p.noBets[msg.sender] == 0) {
                p.noBettors.push(msg.sender);
            }
            p.noBets[msg.sender] += msg.value;
            p.totalNo += msg.value;
        }

        emit BetPlaced(_id, msg.sender, _vote, msg.value);
    }

    // 3. Resolve Prediction
    function resolvePrediction(string memory _id, bool _result) external onlyAdminOrOperator {
        require(predictionExists[_id], "Prediction does not exist");
        Prediction storage p = predictions[_id];
        require(!p.resolved, "Already resolved");
        
        p.resolved = true;
        p.result = _result;

        // Calculate Fees
        uint256 totalPool = p.totalYes + p.totalNo;
        uint256 platformFee = (totalPool * platformFeeBps) / 10000;
        uint256 creatorFee = (totalPool * creatorFeeBps) / 10000;
        
        // Send Platform Fee
        (bool sentHouse, ) = admin.call{value: platformFee}("");
        require(sentHouse, "Failed to send platform fee");

        // Send Creator Fee (if creator exists and isn't admin/null)
        if (p.creator != address(0) && p.creator != admin) {
             (bool sentCreator, ) = p.creator.call{value: creatorFee}("");
             require(sentCreator, "Failed to send creator fee");
        } else {
            // If self-created by admin, keep fee or burn? Keep in contract or treat as revenue?
            // Sending to admin for simplicity
            (bool sentAdmin, ) = admin.call{value: creatorFee}("");
            require(sentAdmin, "Failed to send creator fee to admin");
        }
        
        // Distributable = Total - 25% (20+5)
        uint256 totalFees = platformFee + creatorFee;

        emit PredictionResolved(_id, _result, totalPool - totalFees, platformFee, creatorFee);
    }

    // 3.5. Resolve as Void (Draw/Refund)
    function resolveVoid(string memory _id) external onlyAdmin {
        require(predictionExists[_id], "Prediction does not exist");
        Prediction storage p = predictions[_id];
        require(!p.resolved, "Already resolved");

        p.resolved = true;
        p.isVoid = true; // Mark as void

        uint256 totalPool = p.totalYes + p.totalNo;
        uint256 fee = (totalPool * platformFeeBps) / 10000;
        // No creator fee on void? Or yes? Usually void returns 100% or keeps fees.
        // Standard: Users prefer 100% refund. But to prevent spam, maybe fees kept?
        // Current implementation: Keeps platform fee, refunds rest.
        
        (bool sent, ) = admin.call{value: fee}("");
        require(sent, "Failed to send platform fee");

        emit PredictionVoided(_id, totalPool - fee, fee);
    }

    // 4. Distribute Winnings (Batch Processing)
    function distributeWinnings(string memory _id, uint256 _batchSize) external onlyAdminOrOperator {
        Prediction storage p = predictions[_id];
        require(p.resolved, "Prediction not resolved yet");
        require(!p.paidOut, "Already fully paid out");

        address[] memory winners; // Users who have SHARES
        
        uint256 totalPool = p.totalYes + p.totalNo;
        uint256 platformFee = (totalPool * platformFeeBps) / 10000;
        uint256 creatorFee = (totalPool * creatorFeeBps) / 10000;
        uint256 distributablePot = totalPool - platformFee - creatorFee;

        if (p.isVoid) {
            // VOID LOGIC (Refunding Everyone)
            // Distribute Pot is effectively Total - Fees.
            // ... (Same Void Logic as before, just treating refund proportional to bet)
            
             uint256 totalYesCount = p.yesBettors.length;
             uint256 totalNoCount = p.noBettors.length;
             uint256 totalBettors = totalYesCount + totalNoCount;
            
             uint256 endIndex = p.processedIndex + _batchSize;
             if (endIndex > totalBettors) endIndex = totalBettors;

             for (uint256 i = p.processedIndex; i < endIndex; i++) {
                 address userAddr;
                 uint256 betAmount;
                
                 if (i < totalYesCount) {
                     userAddr = p.yesBettors[i];
                     betAmount = p.yesBets[userAddr];
                     p.yesBets[userAddr] = 0; 
                 } else {
                     uint256 noIndex = i - totalYesCount;
                     userAddr = p.noBettors[noIndex];
                     betAmount = p.noBets[userAddr];
                     p.noBets[userAddr] = 0;
                 }

                 if (betAmount > 0) {
                     // Void Refund: Proportional to what's left after fee logic
                     // Or 100% if we didn't take fees (currently we took platform fee).
                     // If seed existed, seed is also lost (or refunded to whom? Creator?).
                     // Current Logic: Only 'Bettors' in the list get refunded.
                     // Seed amount is basically 'donated' to the void refund pool if creator isn't in list.
                     // This is acceptable for Dead Liquidity.
                     
                     uint256 totalUserBets = totalPool - p.seedYes - p.seedNo; // Total Claimable
                     if (totalUserBets > 0) {
                        uint256 refund = (betAmount * distributablePot) / totalUserBets;
                        (bool sent, ) = userAddr.call{value: refund}("");
                        require(sent, "Transfer failed"); 
                        emit PayoutDistributed(_id, userAddr, refund);
                     }
                 }
             }
            
             p.processedIndex = endIndex;
             if (p.processedIndex >= totalBettors) {
                 p.paidOut = true;
                 emit DistributionCompleted(_id);
             }
             return;
        }

        // --- WINNER LOGIC ---
        
        uint256 winningPoolTotal; // Including Seed
        uint256 winningSeed;
        
        if (p.result) { // YES won
            winners = p.yesBettors;
            winningPoolTotal = p.totalYes;
            winningSeed = p.seedYes;
        } else { // NO won
            winners = p.noBettors;
            winningPoolTotal = p.totalNo;
            winningSeed = p.seedNo;
        }
        
        // Critical Fix for Dead Liquidity:
        // The shares that are eligible for payout = Total - Seed.
        uint256 eligibleShares = winningPoolTotal - winningSeed;

        if (winners.length == 0 || eligibleShares == 0) {
            p.paidOut = true;
            return;
        }

        uint256 totalWinners = winners.length;
        uint256 endNormalIndex = p.processedIndex + _batchSize;
        if (endNormalIndex > totalWinners) endNormalIndex = totalWinners;

        for (uint256 i = p.processedIndex; i < endNormalIndex; i++) {
            address winnerAddr = winners[i];
            uint256 betAmount;
            
            if (p.result) {
                betAmount = p.yesBets[winnerAddr];
                p.yesBets[winnerAddr] = 0; 
            } else {
                betAmount = p.noBets[winnerAddr];
                p.noBets[winnerAddr] = 0;
            }

            if (betAmount > 0) {
                // Denominator is now ONLY eligible shares (excluding seed)
                // This means the Seed's portion of the win is distributed to these users.
                uint256 payout = (betAmount * distributablePot) / eligibleShares;
                
                (bool sent, ) = winnerAddr.call{value: payout}("");
                require(sent, "Transfer failed");
                emit PayoutDistributed(_id, winnerAddr, payout);
            }
        }

        p.processedIndex = endNormalIndex;

        if (p.processedIndex >= totalWinners) {
            p.paidOut = true;
            emit DistributionCompleted(_id);
        }
    }
}

contracts/PredictionBattleV6.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title PredictionBattleV6
 * @dev V6: Pull Payment Architecture + Decentralized Verification
 *      - 12 Hour Dispute Window
 *      - No Operator/Bot roles (Admin Only for Arbitration)
 *      - claimWinnings: User pulls funds (no batch distribution)
 *      - withdrawSeed: Creator pulls seed only on VOID
 *      - Decentralized functions (propose, challenge) preserved
 */

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

contract PredictionBattleV6 {
    address public admin;
    IERC20 public usdcToken;
    
    // Config
    uint256 public constant DISPUTE_WINDOW = 43200; // 12 Hours
    uint256 public constant FEE_DENOMINATOR = 10000;
    
    // Fee Config
    uint256 public houseFeeBps = 1000;   // 10%
    uint256 public creatorFeeBps = 500;  // 5%
    uint256 public referrerFeeBps = 500; // 5%
    
    // Shares Config (V3 Logic)
    uint256 public constant SHARE_PRECISION = 1e18;
    uint256 public constant MAX_WEIGHT = 150; // 1.5x
    uint256 public constant MIN_WEIGHT = 100; // 1.0x
    
    // House balance (accumulated fees)
    uint256 public houseBalance;
    mapping(address => uint256) public creatorBalance;
    mapping(address => uint256) public rewardsBalance; // Referrer rewards

    struct Market {
        string id;
        address creator;
        string question;
        uint256 creationTime;
        uint256 bonusDuration;
        uint256 deadline;
        MarketState state;
        bool result;
        bool isVoid;
        
        // Proposal Info
        address proposer;
        bool proposedResult;
        uint256 proposalTime;
        uint256 bondAmount;
        string evidenceUrl;
        
        // Dispute Info
        address challenger;
        uint256 challengeBondAmount;
        string challengeEvidenceUrl;
        uint256 challengeTime;
        
        // Pool Tracking
        uint256 totalYes;
        uint256 totalNo;
        uint256 seedYes;
        uint256 seedNo;
        
        // Shares Tracking
        uint256 totalSharesYes;
        uint256 totalSharesNo;
        
        // Bettor Lists (Kept for frontend views, but not used for batch payout anymore)
        address[] yesBettors;
        address[] noBettors;
    }
    
    struct UserBet {
        uint256 amount;
        uint256 shares;
        address referrer;
        bool claimed; // New logic for Pull Payment
    }
    
    enum MarketState {
        OPEN,      // 0: Accepting bets
        LOCKED,    // 1: Deadline passed, awaiting outcome
        PROPOSED,  // 2: Outcome reported, in dispute window
        DISPUTED,  // 3: Challenged! Admin must resolve
        RESOLVED   // 4: Final state
    }
    
    mapping(string => Market) public markets;
    mapping(string => bool) public marketExists;
    
    // Bets Mapping
    mapping(string => mapping(address => UserBet)) public yesBets;
    mapping(string => mapping(address => UserBet)) public noBets;

    // Claims Mapping (New)
    // To ensure double safety alongside UserBet.claimed
    mapping(string => mapping(address => bool)) public hasClaimed;
    
    // Locked bonds (balance tracking)
    mapping(address => uint256) public bondBalance;
    
    event MarketCreated(string id, address creator, uint256 deadline, uint256 bonusDuration);
    event BetPlaced(string id, address user, bool vote, uint256 amount, uint256 shares, address referrer, uint256 weight);
    event SeedAdded(string id, uint256 yesAmount, uint256 noAmount);
    
    event OutcomeProposed(string id, address proposer, bool result, uint256 bond, uint256 disputeEnd, string evidence);
    event OutcomeChallenged(string id, address challenger, uint256 bond, string evidence);
    event DisputeResolved(string id, address winner, uint256 totalBondReward, bool finalResult);
    event OutcomeFinalized(string id, address proposer, uint256 reward);
    event MarketResolved(string id, bool result, uint256 winnerPool);
    event MarketVoided(string id);
    event PayoutClaimed(string id, address user, uint256 amount); // Renamed from Distributed
    event SeedWithdrawn(string id, address creator, uint256 amount); // New
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin");
        _;
    }
    
    constructor(address _usdcAddress) {
        admin = msg.sender;
        usdcToken = IERC20(_usdcAddress);
    }
    
    // ============ Core Functions (V3/V5 Compat) ============
    
    function createMarket(
        string memory _id,
        string memory _question,
        uint256 _usdcSeedAmount,
        uint256 _duration,
        uint256 _bonusDuration
    ) external {
        require(!marketExists[_id], "Exists");
        require(_usdcSeedAmount > 0 && _usdcSeedAmount % 2 == 0, "Invalid seed");
        
        require(usdcToken.transferFrom(msg.sender, address(this), _usdcSeedAmount), "Transfer failed");
        
        Market storage m = markets[_id];
        m.id = _id;
        m.creator = msg.sender;
        m.question = _question;
        m.creationTime = block.timestamp;
        m.bonusDuration = _bonusDuration > 0 ? _bonusDuration : _duration;
        m.deadline = block.timestamp + _duration;
        m.state = MarketState.OPEN;
        
        uint256 seedPerSide = _usdcSeedAmount / 2;
        m.totalYes = seedPerSide;
        m.totalNo = seedPerSide;
        m.seedYes = seedPerSide;
        m.seedNo = seedPerSide;
        
        marketExists[_id] = true;
        emit MarketCreated(_id, msg.sender, m.deadline, _bonusDuration);
        emit SeedAdded(_id, seedPerSide, seedPerSide);
    }
    
    function placeBet(
        string memory _marketId,
        bool _side,
        uint256 _usdcAmount,
        address _referrer
    ) external {
        require(marketExists[_marketId], "No market");
        Market storage m = markets[_marketId];
        require(m.state == MarketState.OPEN, "Not open");
        require(block.timestamp < m.deadline, "Expired");
        require(_usdcAmount > 0, "Zero amount");
        
        require(usdcToken.transferFrom(msg.sender, address(this), _usdcAmount), "Transfer failed");
        
        // Fee Logic
        uint256 houseFee = (_usdcAmount * houseFeeBps) / FEE_DENOMINATOR;
        uint256 creatorFee = (_usdcAmount * creatorFeeBps) / FEE_DENOMINATOR;
        uint256 referrerFee = 0;
        
        if (_referrer != address(0) && _referrer != msg.sender) {
             referrerFee = (_usdcAmount * referrerFeeBps) / FEE_DENOMINATOR;
             rewardsBalance[_referrer] += referrerFee;
        } else {
             // If no referrer, house takes the referrer portion
             houseFee += (_usdcAmount * referrerFeeBps) / FEE_DENOMINATOR;
        }
        
        houseBalance += houseFee;
        creatorBalance[m.creator] += creatorFee;
        
        uint256 netBet = _usdcAmount - houseFee - creatorFee - referrerFee;
        
        // Shares Logic
        uint256 currentWeight = _calculateWeight(m.creationTime, m.bonusDuration);
        uint256 shares = (netBet * currentWeight * SHARE_PRECISION) / 100;
        
        if (_side) {
            if (yesBets[_marketId][msg.sender].amount == 0) m.yesBettors.push(msg.sender);
            yesBets[_marketId][msg.sender].amount += netBet;
            yesBets[_marketId][msg.sender].shares += shares;
            m.totalYes += netBet;
            m.totalSharesYes += shares;
        } else {
            if (noBets[_marketId][msg.sender].amount == 0) m.noBettors.push(msg.sender);
            noBets[_marketId][msg.sender].amount += netBet;
            noBets[_marketId][msg.sender].shares += shares;
            m.totalNo += netBet;
            m.totalSharesNo += shares;
        }
        
        emit BetPlaced(_marketId, msg.sender, _side, _usdcAmount, shares, _referrer, currentWeight);
    }
    
    function _calculateWeight(uint256 _creationTime, uint256 _bonusDuration) internal view returns (uint256) {
        uint256 elapsed = block.timestamp > _creationTime ? block.timestamp - _creationTime : 0;
        if (elapsed >= _bonusDuration) return MIN_WEIGHT;
        uint256 bonus = ((MAX_WEIGHT - MIN_WEIGHT) * (_bonusDuration - elapsed)) / _bonusDuration;
        return MIN_WEIGHT + bonus;
    }

    // ============ PROPOSAL & DISPUTE FLOW (Decentralized) ============
    
    function getRequiredBond(string memory _id) public view returns (uint256) {
        Market storage m = markets[_id];
        uint256 pool = m.totalYes + m.totalNo;
        uint256 bond = pool / 10; // 10%
        if (bond < 5 * 10**6) return 5 * 10**6; // Min 5 USDC
        return bond;
    }

    function proposeOutcome(string memory _marketId, bool _result, string memory _evidenceUrl) external {
        require(marketExists[_marketId], "No market");
        Market storage m = markets[_marketId];
        
        // Allow propose if OPEN or LOCKED (V5 logic)
        require(m.state == MarketState.OPEN || m.state == MarketState.LOCKED, "Not active");
        
        uint256 bondAmount = getRequiredBond(_marketId);
        require(usdcToken.transferFrom(msg.sender, address(this), bondAmount), "Bond transfer failed");
        
        m.proposer = msg.sender;
        m.proposedResult = _result;
        m.proposalTime = block.timestamp;
        m.bondAmount = bondAmount;
        m.evidenceUrl = _evidenceUrl;
        m.state = MarketState.PROPOSED;
        
        bondBalance[msg.sender] += bondAmount;
        
        emit OutcomeProposed(_marketId, msg.sender, _result, bondAmount, block.timestamp + DISPUTE_WINDOW, _evidenceUrl);
    }
    
    function challengeOutcome(string memory _marketId, string memory _evidenceUrl) external {
        require(marketExists[_marketId], "No market");
        Market storage m = markets[_marketId];
        
        require(m.state == MarketState.PROPOSED, "Not proposed");
        require(block.timestamp <= m.proposalTime + DISPUTE_WINDOW, "Dispute window closed");
        require(msg.sender != m.proposer, "Cannot challenge self");
        
        uint256 bondAmount = m.bondAmount; // Match proposer bond
        require(usdcToken.transferFrom(msg.sender, address(this), bondAmount), "Bond transfer failed");
        
        m.challenger = msg.sender;
        m.challengeBondAmount = bondAmount;
        m.challengeEvidenceUrl = _evidenceUrl;
        m.challengeTime = block.timestamp;
        
        bondBalance[msg.sender] += bondAmount;
        m.state = MarketState.DISPUTED;
        
        emit OutcomeChallenged(_marketId, msg.sender, bondAmount, _evidenceUrl);
    }
    
    function resolveDispute(string memory _marketId, address _winnerAddress, bool _finalResult) external onlyAdmin { // No Operator
        require(marketExists[_marketId], "No market");
        Market storage m = markets[_marketId];
        require(m.state == MarketState.DISPUTED, "Not in dispute");
        require(_winnerAddress == m.proposer || _winnerAddress == m.challenger, "Invalid winner");
        
        uint256 totalBond = m.bondAmount + m.challengeBondAmount;
        
        bondBalance[m.proposer] -= m.bondAmount;
        bondBalance[m.challenger] -= m.challengeBondAmount;
        
        require(usdcToken.transfer(_winnerAddress, totalBond), "Bond reward failed");
        
        m.state = MarketState.RESOLVED;
        m.result = _finalResult;
        
        m.bondAmount = 0;
        m.challengeBondAmount = 0;
        
        emit DisputeResolved(_marketId, _winnerAddress, totalBond, _finalResult);
        emit MarketResolved(_marketId, _finalResult, _finalResult ? m.totalYes : m.totalNo);
    }
    
    function finalizeOutcome(string memory _marketId) external {
        require(marketExists[_marketId], "No market");
        Market storage m = markets[_marketId];
        
        require(m.state == MarketState.PROPOSED, "Not proposed");
        require(block.timestamp > m.proposalTime + DISPUTE_WINDOW, "Window active");
        
        address proposer = m.proposer;
        uint256 bondAmount = m.bondAmount;
        
        bondBalance[proposer] -= bondAmount;
        require(usdcToken.transfer(proposer, bondAmount), "Bond return failed");
        
        // Reporter Reward (1% of total pool)
        uint256 reward = (m.totalYes + m.totalNo) / 100; 
        if (reward > 0 && houseBalance >= reward) {
            houseBalance -= reward;
            usdcToken.transfer(proposer, reward);
        }
        
        m.state = MarketState.RESOLVED;
        m.result = m.proposedResult;
        
        emit OutcomeFinalized(_marketId, proposer, reward);
        emit MarketResolved(_marketId, m.result, m.result ? m.totalYes : m.totalNo);
    }

    // ============ PULL PAYMENT (V6 MAIN CHANGE) ============
    
    function claimWinnings(string memory _id) external {
        require(marketExists[_id], "No market");
        Market storage m = markets[_id];
        require(m.state == MarketState.RESOLVED, "Not resolved");
        
        require(!hasClaimed[_id][msg.sender], "Already claimed");
        
        uint256 payout = 0;
        UserBet storage yesBet = yesBets[_id][msg.sender];
        UserBet storage noBet = noBets[_id][msg.sender];
        
        if (m.isVoid) {
            // Void: Refund Net Bet (Fees were taken at entry and not refunded to prevent drain)
            payout = yesBet.amount + noBet.amount;
        } else {
             bool isYesWinner = m.result;
             UserBet storage winningBet = isYesWinner ? yesBet : noBet;
             
             if (winningBet.amount > 0 && winningBet.shares > 0) {
                 uint256 totalPool = m.totalYes + m.totalNo;
                 // Fees were already sent/stored during placeBet, so we don't deduct them again here.
                 // We need to calculate what remains in the contract (Net Bets + Seeds).
                 // Actually simpler: V3 Logic implies the Pool available for distribution is (TotalYes + TotalNo) - Fees.
                 
                 // Re-calculate fees to know what the 'Distributable Pot' is.
                 // Note: Fees are already taken out of contract usage or stored in balances.
                 // The contract balance has (NetBets + Seeds + AccruedFees + Bonds).
                 // We should distribute the portion that corresponds to the Winning Pot.
                 
                 uint256 fee = (totalPool * (houseFeeBps + creatorFeeBps + referrerFeeBps)) / FEE_DENOMINATOR;
                 uint256 distributablePool = totalPool - fee; 
                 
                 // Payout = (UserShares * DistributablePool) / TotalWinningShares
                 uint256 totalWinningShares = isYesWinner ? m.totalSharesYes : m.totalSharesNo;
                 
                 if (totalWinningShares > 0) {
                     payout = (winningBet.shares * distributablePool) / totalWinningShares;
                 }
             }
        }
        
        require(payout > 0, "Nothing to claim");
        
        hasClaimed[_id][msg.sender] = true;
        // Also update struct fallback
        if (yesBet.amount > 0) yesBet.claimed = true;
        if (noBet.amount > 0) noBet.claimed = true;
        
        require(usdcToken.transfer(msg.sender, payout), "Transfer failed");
        
        emit PayoutClaimed(_id, msg.sender, payout);
    }
    
    // ============ CREATOR SEED WITHDRAW (VOID ONLY) ============
    // Addresses the gap where Creators lose seed on Void because they have no shares.
    
    function withdrawSeed(string memory _id) external {
         require(marketExists[_id], "No market");
         Market storage m = markets[_id];
         require(m.state == MarketState.RESOLVED, "Not resolved");
         require(m.isVoid, "Market not void");
         require(msg.sender == m.creator, "Only creator");
         require(!hasClaimed[_id][msg.sender], "Already withdrawn");
         
         uint256 totalSeed = m.seedYes + m.seedNo;
         require(totalSeed > 0, "No seed");
         
         hasClaimed[_id][msg.sender] = true;
         
         require(usdcToken.transfer(msg.sender, totalSeed), "Transfer failed");
         emit SeedWithdrawn(_id, msg.sender, totalSeed);
    }
    
    // ============ FEE WITHDRAWALS ============

    function withdrawCreatorFees() external {
        uint256 amount = creatorBalance[msg.sender];
        require(amount > 0, "No fees");
        creatorBalance[msg.sender] = 0;
        require(usdcToken.transfer(msg.sender, amount), "Transfer failed");
    }

    function withdrawReferrerFees() external {
        uint256 amount = rewardsBalance[msg.sender];
        require(amount > 0, "No fees");
        rewardsBalance[msg.sender] = 0;
        require(usdcToken.transfer(msg.sender, amount), "Transfer failed");
    }
    
    // ============ Emergency / Admin ============
    
    function adminResolve(string memory _marketId, bool _result) external onlyAdmin {
        Market storage m = markets[_marketId];
        require(m.state != MarketState.RESOLVED, "Resolved");
        
        // Return bonds if any
        if (m.bondAmount > 0) { 
             bondBalance[m.proposer] -= m.bondAmount;
             usdcToken.transfer(m.proposer, m.bondAmount);
        }
        if (m.challengeBondAmount > 0) {
             bondBalance[m.challenger] -= m.challengeBondAmount;
             usdcToken.transfer(m.challenger, m.challengeBondAmount);
        }
        
        m.state = MarketState.RESOLVED;
        m.result = _result;
        emit MarketResolved(_marketId, _result, 0); // Pool handled in claim layer
    }
    
    function voidMarket(string memory _marketId) external onlyAdmin {
        Market storage m = markets[_marketId];
        require(m.state != MarketState.RESOLVED, "Resolved");
        
        if (m.bondAmount > 0) {
             bondBalance[m.proposer] -= m.bondAmount;
             usdcToken.transfer(m.proposer, m.bondAmount);
        }
        if (m.challengeBondAmount > 0) {
             bondBalance[m.challenger] -= m.challengeBondAmount;
             usdcToken.transfer(m.challenger, m.challengeBondAmount);
        }
        
        m.state = MarketState.RESOLVED;
        m.isVoid = true;
        emit MarketVoided(_marketId);
    }
}

